import React, { useState } from "react";
import styles from "./FoodCon.module.css";
import Item from "./Item";
import Backdrop from "./Backdrop";
import ReactDom from "react-dom";

const FoodCon = (props) => {
  const [myShopingList, setShopingList] = useState([]);
  const myList = [
    { name: "pitza", id: 1, price: 10.5, des: "the best pitza in the country" },
    { name: "flafl", id: 2, price: 2, des: "the best flafl in the country" },
    {
      name: "shawrma",
      id: 3,
      price: 6,
      des: "the best shawrma in the country",
    },
    {
      name: "burger",
      id: 4,
      price: 7.4,
      des: "the best burger in the country",
    },
  ];
  const shopingListHandler = (item) => {
    let ok = false;
    setShopingList((prev) => {
      prev.forEach((el) => {
        if (el.id === item.id) {
          el.miniCounter = +el.miniCounter + +item.miniCounter;
          ok = true;
        }
      });
      if (ok) {
        return [...prev];
      } else {
        return [...prev, item];
      }
    });
  };
  const updateTheOverLay=(sign,id)=>{
    
    setShopingList(prev=>{
      prev.forEach(el=>{
        if(el.id===id){
          if(sign==="+"){
            el.miniCounter= +el.miniCounter +1;
          }
          if(sign==="-"){
            if(el.miniCounter===1){
              
              
            }else{
              
              el.miniCounter= +el.miniCounter -1;
            }
          }
        }
      })
      return [...prev]
    })

  }
  const removeElFromOverLay=(id)=>{
    setShopingList(prev=>{
      const result=prev.filter(el=>{
        if(el.id===id){
          return false;
        }
        else{
          return true;
        }
      })
      return [...result]
    })
  }
  console.log(myShopingList);
  return (
    <div className={styles.contener}>
      {ReactDom.createPortal(
        <Backdrop
          myShopingList={myShopingList}
          apperBack={props.apperBack}
          backdropApper={props.backdropApper}
          updateTheOverLay={updateTheOverLay}
          removeElFromOverLay={removeElFromOverLay}
          decrementAmount={props.decrementAmount}
        ></Backdrop>,
        document.body
      )}

      {myList.map((el) => {
        return (
          <Item
            price={el.price}
            des={el.des}
            key={el.id}
            id={el.id}
            name={el.name}
            updateAmount={props.updateAmount}
            trigerAnimation={props.trigerAnimation}
            shopingListHandler={shopingListHandler}
          ></Item>
        );
      })}
    </div>
  );
};

export default FoodCon;
