import React,{useContext} from "react";
import MyContext from "./store/myContext";
import styles from "./Backdrop.module.css";
import InnerItem from "./InnerItem";
const Backdrop = (props) => {
  const context=useContext(MyContext);
  const closeClickHandler=(e)=>{
   if(e.target!==e.currentTarget){
      return;
    }
    context.backdropApper(false);
  }

 const myTotal= context.myShopingList.reduce((toatal,el)=>{
    return toatal+ (+el.price * +el.miniCounter);
  },0)

  
    const backdropStyle=context.apperBack?styles.backdroppls:styles.backdrop;
  return (
    <div className={backdropStyle} onClick={closeClickHandler}>
      <div className={styles.overlay}>
        {context.myShopingList.map((el) => {
          return (
            <InnerItem
              name={el.name}
              key={el.id}
              id={el.id}
              price={el.price}
              miniCounter={el.miniCounter}
              // updateTheOverLay={props.updateTheOverLay}
              // removeElFromOverLay={props.removeElFromOverLay}
             
            ></InnerItem>
          );
        })}
        <div className={styles.controls}>
          <h3>total amount</h3>
          <span>${myTotal}</span>
          <div className={styles.buttons}>
            <button onClick={closeClickHandler}>close</button>
            <button >order</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Backdrop;
