      myList.push(myMeal);
      counter=counter+1;
    }
    setList(myList);
    console.log(myList);
  };
  
const fetchMyData=useCallback(fetchData,[myList]);


useEffect(()=>{
  fetchMyData();
},[fetchMyData])




  console.log("dfsfs",myList);
