import React from "react";
import styles from "./Backdrop.module.css";
import InnerItem from "./InnerItem";
const Backdrop = (props) => {
  const closeClickHandler=(e)=>{
   if(e.target!==e.currentTarget){
      return;
    }
    props.backdropApper(false);
  }
  // const backdropClickHandler=(e)=>{
    
  //   props.backdropApper(false);
  // }
 const myTotal= props.myShopingList.reduce((toatal,el)=>{
    return toatal+ (+el.price * +el.miniCounter);
  },0)

  // setTotalPrice(myTotal);
    const backdropStyle=props.apperBack?styles.backdroppls:styles.backdrop;
  return (
    <div className={backdropStyle} onClick={closeClickHandler}>
      <div className={styles.overlay}>
        {props.myShopingList.map((el) => {
          return (
            <InnerItem
              name={el.name}
              key={el.id}
              id={el.id}
              price={el.price}
              miniCounter={el.miniCounter}
              updateTheOverLay={props.updateTheOverLay}
            ></InnerItem>
          );
        })}
        <div className={styles.controls}>
          <h3>total amount</h3>
          <span>${myTotal}</span>
          <div className={styles.buttons}>
            <button onClick={closeClickHandler}>close</button>
            <button >order</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Backdrop;
