import React, { useState } from "react";
import styles from "./FoodCon.module.css";
import Item from "./Item";
import Backdrop from "./Backdrop";
import ReactDom from "react-dom";

const FoodCon = (props) => {
  const [myShopingList, setShopingList] = useState([]);
  const myList = [
    { name: "pitza", id: 1, price: 10.5, des: "the best pitza in the country" },
    { name: "flafl", id: 2, price: 2.5, des: "the best flafl in the country" },
    {
      name: "shawrma",
      id: 3,
      price: 6,
      des: "the best shawrma in the country",
    },
    {
      name: "burger",
      id: 4,
      price: 7.4,
      des: "the best burger in the country",
    },
  ];
  const shopingListHandler = (item) => {
    let ok = false;
    setShopingList((prev) => {
      prev.forEach((el) => {
        if (el.id === item.id) {
          el.miniCounter = +el.miniCounter + +item.miniCounter;
          // console.log(el.price,"price");
          if (el.name === "pitza") {
            el.price = 10.5 * +el.miniCounter;
          }
          // console.log(el.price,"price");
          ok = true;
        }
      });
      if (ok) {
        return [...prev];
      } else {
        return [...prev, item];
      }
    });
  };
  console.log(myShopingList);
  return (
    <div className={styles.contener}>
      {ReactDom.createPortal(
        <Backdrop
          myShopingList={myShopingList}
          apperBack={props.apperBack}
        ></Backdrop>,
        document.body
      )}

      {myList.map((el) => {
        return (
          <Item
            price={el.price}
            des={el.des}
            key={el.id}
            id={el.id}
            name={el.name}
            updateAmount={props.updateAmount}
            trigerAnimation={props.trigerAnimation}
            shopingListHandler={shopingListHandler}
          ></Item>
        );
      })}
    </div>
  );
};

export default FoodCon;
