import React,{useState} from "react";

const MyContext = React.createContext({
  amount: "0",
  apperBack: false,
  animationVald: false,
  myShopingList:[],
  backdropApper:(vald)=>{},
  updateAmount:(newVal)=>{},
  decrementAmount:(sign)=>{},
  trigerAnimation:(vald)=>{},
  shopingListHandler:(item)=>{},
  updateTheOverLay:(sign,id)=>{},
  removeElFromOverLay:(id)=>{}
});

export default MyContext;

export const MyContectProvider=(props)=>{
    const [amount, setAmount] = useState("0");
    const [apperBack, setApperBack] = useState(false);
    const [animationVald, setAnimationVald] = useState(false);
    const [myShopingList, setShopingList] = useState([]);
    const backdropApper = (vald) => {
      setApperBack(vald);
    };
    const updateAmount = (newVal) => {
      setAmount((prev) => {
        return +prev + +newVal;
      });
    };
    const decrementAmount = (sign) => {
      if (sign === "+") {
        setAmount((prev) => {
          return +prev + 1;
        });
      } else {
        setAmount((prev) => {
          return +prev - 1;
        });
      }
    };
    const trigerAnimation = (vald) => {
      setAnimationVald(vald);
    };
    const shopingListHandler = (item) => {
        let ok = false;
        setShopingList((prev) => {
          prev.forEach((el) => {
            if (el.id === item.id) {
              el.miniCounter = +el.miniCounter + +item.miniCounter;
              ok = true;
            }
          });
          if (ok) {
            return [...prev];
          } else {
            return [...prev, item];
          }
        });
      };
      const updateTheOverLay=(sign,id)=>{
        
        setShopingList(prev=>{
          prev.forEach(el=>{
            if(el.id===id){
              if(sign==="+"){
                el.miniCounter= +el.miniCounter +1;
              }
              if(sign==="-"){
                if(el.miniCounter===1){
                  
                  
                }else{
                  
                  el.miniCounter= +el.miniCounter -1;
                }
              }
            }
          })
          return [...prev]
        })
    
      }
      const removeElFromOverLay=(id)=>{
        setShopingList(prev=>{
          const result=prev.filter(el=>{
            if(el.id===id){
              return false;
            }
            else{
              return true;
            }
          })
          return [...result]
        })
      }
    return(
        <MyContext.Provider value={{
            amount: amount,
            apperBack: apperBack,
            animationVald: animationVald,
            myShopingList:myShopingList,
            backdropApper:backdropApper,
            updateAmount:updateAmount,
            decrementAmount:decrementAmount,
            trigerAnimation:trigerAnimation,
            shopingListHandler:shopingListHandler,
            updateTheOverLay:updateTheOverLay,
            removeElFromOverLay:removeElFromOverLay
          }}>
           {props.children}
        </MyContext.Provider>
    )
}
