import { Fragment } from "react";
import styles from './InputItem.module.css';

const InputItem = (props) => {

  return (
    <Fragment>
      <label htmlFor={props.id}>{props.label}</label>
      <input
        type="text"
        id={props.id}
        onChange={props.changeNameHandler}
        onBlur={props.blurNameHandler}
        value={props.name}
        className={!props.nameValid && props.nameToutch ? styles.error : ""}
      />
      {!props.nameValid && props.nameToutch && <p>{props.valdtionMessge}</p>}
    </Fragment>
  );
};

export default InputItem;
