import Header from "./componints/Header";
import Discription from "./componints/Discription";
import FoodCon from "./componints/FoodCon";
function App() {
  const [amount,setAmount]=useState("0");
  const updateAmount=(newVal)=>{
     setAmount(prev=>{
       return (+prev)+(+newVal);
     })
    }
    console.log(amount);
  return (
    <React.Fragment>
       <Header amount={amount}></Header>
       <Discription></Discription>
       <FoodCon updateAmount={updateAmount}></FoodCon>
    </React.Fragment>
  );
}

export default App;
