import React from "react";
import styles from "./Cart.module.css";
const Cart=(props)=>{
    // const [backdropApper,setBackropApper]=useState(false);
    const cartClickHandler=()=>{
    //   setBackropApper(true);
      props.backdropApper(true);
    }
    const cartStyle=props.animationVald?styles.cartpls:styles.cart;
     
    return(
        <div className={cartStyle} onClick={cartClickHandler}>
            your cart
            <span className={styles.counter}>{props.amount}</span>
        </div>
    )
}

export default Cart;
