import React from "react";
import styles from "./FoodCon.module.css";
import Item from "./Item";
import ReactDom from "react-dom";
const Backdrop = () => {
  return <div className={styles.backdrop}></div>;
};
const OverLay=()=>{
  return(
    <div className={styles.overlay}></div>
  )
}
const FoodCon = (props) => {
  const myList = [
    { name: "pitza", id: 1, price: 10.5, des: "the best pitza in the country" },
    { name: "flafl", id: 2, price: 2.5, des: "the best flafl in the country" },
    {
      name: "shawrma",
      id: 3,
      price: 6,
      des: "the best shawrma in the country",
    },
    {
      name: "burger",
      id: 4,
      price: 7.4,
      des: "the best burger in the country",
    },
  ];
  return (
    <div className={styles.contener}>
      {ReactDom.createPortal(<Backdrop></Backdrop>,document.body)}
      {ReactDom.createPortal(<OverLay></OverLay>,document.body)}
      {myList.map((el) => {
        return (
          <Item
            price={el.price}
            des={el.des}
            key={el.id}
            name={el.name}
            updateAmount={props.updateAmount}
            trigerAnimation={props.trigerAnimation}
          ></Item>
        );
      })}
    </div>
  );
};

export default FoodCon;
