import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { uiSliceActions } from "../../store/ui-slice";
import styles from "./OrderingForm.module.css";
import InputItem from "./InputItem";
const OrderingForm = (props) => {
  const myShopingList = useSelector((state) => state.shopingList.myShopingList);
  const dispatch = useDispatch();
  const [name, setName] = useState("");
  const [nameToutch, setNameToutch] = useState(false);
  const [street, setStreet] = useState("");
  const [streetToutch, setStreetTouch] = useState(false);
  const [postal, setPostal] = useState("");
  const [postalToutch, setPostaltoutch] = useState(false);
  const [city, setCity] = useState("");
  const [cityToutch, setCityToutch] = useState(false);
  const [isLoading, setLoading] = useState(false);
  const [hasError, setError] = useState(false);

  async function sendData(info) {
    try {
      setLoading(true);
      props.submitingHandler(true);
      const res = await fetch(
        "https://react-66e3f-default-rtdb.firebaseio.com/infos.json",
        {
          method: "POST",
          body: JSON.stringify({ ...info, myElments: myShopingList }),
          headers: { "contint-type": "application/json" },
        }
      );

      if (!res.ok) {
        throw new Error("rong");
      }
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
    props.submitingHandler(false);
    props.submitedHandler(true);
  }
  const nameValid = name.trim() !== "";
  const streetValid = street.trim() !== "";
  const postalValid =
    postal.trim() !== "" && postal.length === 4 && !isNaN(+postal);
  const cityValid = city.trim() !== "";
  const formValid = nameValid && streetValid && cityValid && postalValid;
  let postalContent;
  if (postal.trim() === "") {
    postalContent = <p>postal must not be empity</p>;
  } else if (isNaN(+postal)) {
    postalContent = <p>postal must be a number</p>;
  } else {
    postalContent = <p>postal must be 4 chracter</p>;
  }
  const changeNameHandler = (e) => {
    setName(e.target.value);
  };
  const blurNameHandler = () => {
    setNameToutch(true);
  };
  const changeStreetHandler = (e) => {
    setStreet(e.target.value);
  };
  const blurStreetHandler = () => {
    setStreetTouch(true);
  };
  const changePostalHandler = (e) => {
    setPostal(e.target.value);
  };
  const blurPostalHandler = () => {
    setPostaltoutch(true);
  };
  const changeCityHandler = (e) => {
    setCity(e.target.value);
  };
  const blurCityHandler = () => {
    setCityToutch(true);
  };
  const cancelCLickHandler = (e) => {
    e.preventDefault();

    dispatch(uiSliceActions.backdropApper(false));
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setNameToutch(true);
    setStreetTouch(true);
    setPostaltoutch(true);
    setCityToutch(true);
    if (!formValid) {
      return;
    }
    sendData({
      name,
      street,
      postal,
      city,
    });

    setNameToutch(false);
    setStreetTouch(false);
    setPostaltoutch(false);
    setCityToutch(false);
    setName("");
    setStreet("");
    setPostal("");
    setCity("");
    props.submitedHandler(false);
  };
  let button;
  if (hasError) {
    button = <p>error</p>;
  } else {
    button = <button type="submit">Confirm</button>;
  }
  return (
    <form className={styles.myForm} onSubmit={submitHandler}>
      <div className={styles.inputss}>
        <InputItem
          id="Your Name"
          changeHandler={changeNameHandler}
          blurHandler={blurNameHandler}
          value={name}
          valdtionMessge="name must not be empty"
          inputToutch={nameToutch}
          inputValid={nameValid}
        ></InputItem>
        <InputItem
          id="Street"
          changeHandler={changeStreetHandler}
          blurHandler={blurStreetHandler}
          value={street}
          valdtionMessge="street must not be empty"
          inputToutch={ streetToutch}
          inputValid={streetValid}
        ></InputItem>
        <InputItem
          id="Postal Code"
          changeHandler={changePostalHandler}
          blurHandler={blurPostalHandler}
          value={postal}
          valdtionMessge={postalContent}
          inputToutch={postalToutch}
          inputValid={postalValid}
        ></InputItem>
        <InputItem
          id="City"
          changeHandler={changeCityHandler}
          blurHandler={blurCityHandler}
          value={city}
          valdtionMessge="city must not be empty"
          inputToutch={cityToutch}
          inputValid={cityValid}
        ></InputItem>


    
      </div>

      <div className={styles.buttons}>
        <button onClick={cancelCLickHandler}>Cancel</button>
        {isLoading ? <p>loading...</p> : button}
      </div>
    </form>
  );
};

export default OrderingForm;
