import React,{useState,useEffect,useContext} from "react";
import MyContext from "./store/myContext";
import styles from "./Item.module.css";

const Item=(props)=>{
    const context=useContext(MyContext);
    const [inputValue,setMyInpValue]=useState("1");
    useEffect(()=>{
         setTimeout(()=>{

             context.trigerAnimation(false);
         },500)
    })
    const inputHandler=(e)=>{
        setMyInpValue(e.target.value);
    }
    const submitHandler=(e)=>{
        e.preventDefault();
        context.trigerAnimation(true);
      context.updateAmount(inputValue)
      props.shopingListHandler({
          id:props.id,
          name:props.name,
          price:props.price,
          miniCounter:inputValue
      });
    }
    return(
        <div className={styles.contener}>
            <h3>{props.name}</h3>
            <p>{props.des}</p>
            <span>${props.price}</span>
            <form className={styles.myform} onSubmit={submitHandler}>
                <label>Amount</label>
                <input type="number" min="1" onChange={inputHandler} value={inputValue}></input>
                <button type="submit">+ Add</button>
            </form>
        </div>
    )
}

export default Item;
