import React, { useContext, useState } from "react";
import styles from "./OrderingForm.module.css";
import MyContext from "./store/myContext";
const OrderingForm = (props) => {
  const context = useContext(MyContext);
  const [name, setName] = useState("");
  const [nameToutch, setNameToutch] = useState(false);
  const [street, setStreet] = useState("");
  const [streetToutch, setStreetTouch] = useState(false);
  const [postal, setPostal] = useState("");
  const [postalToutch, setPostaltoutch] = useState(false);
  const [city, setCity] = useState("");
  const [cityToutch, setCityToutch] = useState(false);
  const [isLoading, setLoading] = useState(false);
  const [hasError, setError] = useState(false);
  // const myElments=context.myShopingList;
  console.log(context.myShopingList);

  async function sendData(info) {
    try {
      setLoading(true);
      props.submitingHandler(true);
      const res = await fetch(
        "https://react-66e3f-default-rtdb.firebaseio.com/infos.json",
        {
          method: "POST",
          body: JSON.stringify({...info,myElments:context.myShopingList}),
          headers: { "contint-type": "application/json" },
        }
      );

      if (!res.ok) {
        throw new Error("rong");
      }
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
    props.submitingHandler(false);
    props.submitedHandler(true);
  }
  const nameValid = name.trim() !== "";
  const streetValid = street.trim() !== "";
  const postalValid =
    postal.trim() !== "" && postal.length === 4 && !isNaN(+postal);
  const cityValid = city.trim() !== "";
  const formValid = nameValid && streetValid && cityValid && postalValid;
  let postalContent;
  if (postal.trim() === "") {
    postalContent = <p>postal must not be empity</p>;
  } else if (isNaN(+postal)) {
    postalContent = <p>postal must be a number</p>;
  } else {
    postalContent = <p>postal must be 4 chracter</p>;
  }
  const changeNameHandler = (e) => {
    setName(e.target.value);
  };
  const blurNameHandler = () => {
    setNameToutch(true);
  };
  const changeStreetHandler = (e) => {
    setStreet(e.target.value);
  };
  const blurStreetHandler = () => {
    setStreetTouch(true);
  };
  const changePostalHandler = (e) => {
    setPostal(e.target.value);
  };
  const blurPostalHandler = () => {
    setPostaltoutch(true);
  };
  const changeCityHandler = (e) => {
    setCity(e.target.value);
  };
  const blurCityHandler = () => {
    setCityToutch(true);
  };
  const cancelCLickHandler = (e) => {
    e.preventDefault();
    context.backdropApper(false);
  };

  const submitHandler = (e) => {
    e.preventDefault();
    setNameToutch(true);
    setStreetTouch(true);
    setPostaltoutch(true);
    setCityToutch(true);
    if (!formValid) {
      return;
    }
    sendData({
      name,
      street,
      postal,
      city,
    });
    props.submitedHandler(false);
    setNameToutch(false);
    setStreetTouch(false);
    setPostaltoutch(false);
    setCityToutch(false);
    setName("");
    setStreet("");
    setPostal("");
    setCity("");
  };
  let button;
  if (hasError) {
    button = <p>error</p>;
  } else {
    button = <button type="submit">Confirm</button>;
  }
  return (
    <form className={styles.myForm} onSubmit={submitHandler}>
      <div className={styles.inputss}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={changeNameHandler}
          onBlur={blurNameHandler}
          value={name}
          className={!nameValid && nameToutch ? styles.error : ""}
        />
        {!nameValid && nameToutch && <p>name must not be empty</p>}

        <label htmlFor="street">Street</label>
        <input
          type="text"
          id="street"
          onChange={changeStreetHandler}
          onBlur={blurStreetHandler}
          value={street}
          className={!streetValid && streetToutch ? styles.error : ""}
        />
        {!streetValid && streetToutch && <p>street must not be empty</p>}
        <label htmlFor="code">Postal Code</label>
        <input
          type="text"
          id="code"
          onChange={changePostalHandler}
          onBlur={blurPostalHandler}
          value={postal}
          className={!postalValid && postalToutch ? styles.error : ""}
        />
        {!postalValid && postalToutch && postalContent}
        <label htmlFor="city">City</label>
        <input
          type="text"
          id="city"
          onChange={changeCityHandler}
          onBlur={blurCityHandler}
          value={city}
          className={!cityValid && cityToutch ? styles.error : ""}
        />
        {!cityValid && cityToutch && <p>city must not be empty</p>}
      </div>

      <div className={styles.buttons}>
        <button onClick={cancelCLickHandler}>Cancel</button>
        {isLoading ? <p>loading...</p> : button}
      </div>
    </form>
  );
};

export default OrderingForm;
