import React, { useState, useEffect } from "react";
import styles from "./Item.module.css";
import ReactDom from "react-dom";

const Backdrop = () => {
  return <div className={styles.backdrop}></div>;
};
const Item = (props) => {
  const [inputValue, setMyInpValue] = useState("1");
  useEffect(() => {
    setTimeout(() => {
      props.trigerAnimation(false);
    }, 500);
  });
  const inputHandler = (e) => {
    setMyInpValue(e.target.value);
  };
  const submitHandler = (e) => {
    e.preventDefault();
    props.trigerAnimation(true);
    props.updateAmount(inputValue);
  };
  return (
    <React.Fragment>
        {ReactDom.createPortal(<Backdrop></Backdrop>,document.body)}
      <div className={styles.contener}>
        <h3>{props.name}</h3>
        <p>{props.des}</p>
        <span>${props.price}</span>
        <form className={styles.myform} onSubmit={submitHandler}>
          <label>Amount</label>
          <input
            type="number"
            min="1"
            onChange={inputHandler}
            value={inputValue}
          ></input>
          <button type="submit">+ Add</button>
        </form>
      </div>
    </React.Fragment>
  );
};

export default Item;
