 const [inputToutch, setInputToutch] = useState(false);

 const changeHandler = (e) => {
    setInput(e.target.value);
  };
  const blurHandler = () => {
    setInputToutch(true);
  };

let inputValid= input.trim() !== "";
let valdtionMessge=<p>the filed must not be empty</p> 
if(type==="Postal Code"){
    inputValid =
    input.trim() !== "" && input.length === 4 && !isNaN(+input);

    if (input.trim() === "") {
        valdtionMessge = <p>postal must not be empity</p>;
      } else if (isNaN(+input)) {
        valdtionMessge = <p>postal must be a number</p>;
      } else {
        valdtionMessge = <p>postal must be 4 chracter</p>;
      }
}



return [input,inputToutch,changeHandler,blurHandler,inputValid,valdtionMessge]
}

