import { Fragment } from "react";
import styles from "./InputItem.module.css";
import useValdtion from "../../hooks/useValdtion";
const InputItem = (props) => {
  const [
    input,
    inputToutch,
    changeHandler,
    blurHandler,
    inputValid,
    valdtionMessge,
  ] = useValdtion(props.id);

  return (
    <Fragment>
      <label htmlFor={props.id}>{props.id}</label>
      <input
        type="text"
        id={props.id}
        onChange={changeHandler}
        onBlur={blurHandler}
        value={input}
        className={!inputValid && inputToutch ? styles.error : ""}
      />
      {!inputValid && inputToutch && <p>{valdtionMessge}</p>}
    </Fragment>
  );
};

export default InputItem;
