import React, { useState } from "react";
import Header from "./componints/Header";
import Discription from "./componints/Discription";
import FoodCon from "./componints/FoodCon";
function App() {
  const [amount, setAmount] = useState("0");
  const [apperBack, setApperBack] = useState(false);
  const [animationVald, setAnimationVald] = useState(false);
  const backdropApper = (vald) => {
    setApperBack(vald);
  };
  const updateAmount = (newVal) => {
    setAmount((prev) => {
      return +prev + +newVal;
    });
  };
  console.log(amount);
  const trigerAnimation = (vald) => {
    setAnimationVald(vald);
  };
  return (
    <React.Fragment>
      <Header
        amount={amount}
        animationVald={animationVald}
        backdropApper={backdropApper}
      ></Header>
      <Discription></Discription>
      <FoodCon
        updateAmount={updateAmount}
        trigerAnimation={trigerAnimation}
        apperBack={apperBack}
      ></FoodCon>
    </React.Fragment>
  );
}

export default App;
