import React from "react";

import styles from "./FoodCon.module.css";
import Item from "./Item";
import Backdrop from "./Backdrop";
import ReactDom from "react-dom";

const FoodCon = (props) => {
  const myList = [
    { name: "pitza", id: 1, price: 10.5, des: "the best pitza in the country" },
    { name: "flafl", id: 2, price: 2, des: "the best flafl in the country" },
    {
      name: "shawrma",
      id: 3,
      price: 6,
      des: "the best shawrma in the country",
    },
    {
      name: "burger",
      id: 4,
      price: 7.4,
      des: "the best burger in the country",
    },
  ];
//  let myList = [];
//   const fetchData = async () => {
//     const responce = await fetch(
//       "https://react-66e3f-default-rtdb.firebaseio.com/meals.json"
//     );
//     const data = await responce.json();
   
//     for (const id in data) {
//       const myMeal = { id, name: id.name, des: id.des, price: id.price };
//       myList.push(myMeal);
//     }
//   };
//   fetchData();
  // const myList = [];

  return (
    <div className={styles.contener}>
      {ReactDom.createPortal(<Backdrop></Backdrop>, document.body)}

      {myList.map((el) => {
        return (
          <Item
            price={el.price}
            des={el.des}
            key={el.id}
            id={el.id}
            name={el.name}
          ></Item>
        );
      })}
    </div>
  );
};

export default FoodCon;
