    const inputValid = input.trim() !== "";

  return (
    <Fragment>
      <label htmlFor={props.id}>{props.id}</label>
      <input
        type="text"
        id={props.id}
        onChange={changeHandler}
        onBlur={blurHandler}
        value={input}
        className={!inputValid && inputToutch ? styles.error : ""}
      />
      {!inputValid && inputToutch && <p>{props.valdtionMessge}</p>}
    </Fragment>
  );
};

