import React, { useState, useEffect, useCallback } from "react";

import styles from "./FoodCon.module.css";
import Item from "./Item";
import Backdrop from "./Backdrop";
import ReactDom from "react-dom";

const FoodCon = (props) => {
  const [myList, setList] = useState([]);
  const [isLoading, setLoading] = useState(true);

  const fetchData = async () => {
    const responce = await fetch(
      "https://react-66e3f-default-rtdb.firebaseio.com/meals.json"
    );
    const data = await responce.json();
    const meals = [];
    let counter = 1;
    for (const id in data) {
      const myMeal = {
        id: counter,
        name: data[id].name,
        des: data[id].des,
        price: data[id].price,
      };
      meals.push(myMeal);
      counter = counter + 1;
    }

    setList(meals);
  };

  const fetchMyData = useCallback(fetchData, []);

  useEffect(() => {
    fetchMyData();
  }, [fetchMyData]);

  console.log("dfsfs", myList);

  const myContent = isLoading ? (
    <p>lodaing...</p>
  ) : (
    myList.map((el) => {
      return (
        <Item
          price={el.price}
          des={el.des}
          key={el.id}
          id={el.id}
          name={el.name}
        ></Item>
      );
    })
  );

  return (
    <div className={styles.contener}>
      {ReactDom.createPortal(<Backdrop></Backdrop>, document.body)}

      {myContent}
    </div>
  );
};

export default FoodCon;
